import React, { useState, useMemo } from "react";
import "./index.css";

const Table = ({ columns, data }) => {
  const [sortConfig, setSortConfig] = useState({ key: "", direction: "" });
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // Sắp xếp dữ liệu
  const sortedData = useMemo(() => {
    let sortableData = [...data];
    if (sortConfig.key) {
      sortableData.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key])
          return sortConfig.direction === "ascending" ? -1 : 1;
        if (a[sortConfig.key] > b[sortConfig.key])
          return sortConfig.direction === "ascending" ? 1 : -1;
        return 0;
      });
    }
    return sortableData;
  }, [data, sortConfig]);

  // Lọc dữ liệu
  const filteredData = sortedData.filter((item) =>
    columns.some((column) =>
      String(item[column.field])
        .toLowerCase()
        .includes(searchTerm.toLowerCase())
    )
  );

  // Tính tổng số lượng hoặc giá trị
  const totalRow = columns.reduce((acc, column) => {
    if (column.field) {
      acc[column.field] = filteredData.reduce(
        (sum, item) => sum + (item[column.field] || 0),
        0
      );
    }
    return acc;
  }, {});

  totalRow.isSummaryRow = true; // Đánh dấu đây là hàng tổng

  // Dữ liệu sau khi phân trang
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const paginatedData = filteredData.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const requestSort = (key) => {
    let direction = "ascending";
    if (sortConfig.key === key && sortConfig.direction === "ascending") {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  const renderSortArrow = (columnKey) => {
    if (sortConfig.key === columnKey) {
      return sortConfig.direction === "ascending" ? " ↑" : " ↓";
    }
    return null;
  };

  return (
    <div className="table-container">
      {/* Ô tìm kiếm */}
      <input
        type="text"
        placeholder="Tìm kiếm..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />

      {/* Bảng */}
      <table className="table">
        <thead>
          <tr>
            {columns.map((column, index) => (
              <th
                key={index}
                onClick={() => requestSort(column.field)}
                style={{ width: column.width }}
              >
                {column.label}
                {renderSortArrow(column.field)}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {paginatedData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {columns.map((column, colIndex) => (
                <td key={colIndex}>
                  {column.render
                    ? column.render(row[column.field], row)
                    : row[column.field]}
                </td>
              ))}
            </tr>
          ))}

          {/* Hàng Tổng Số */}
          <tr className="summary-row">
            {columns.map((column, colIndex) => (
              <td key={colIndex}>
                {column.render
                  ? column.render(totalRow[column.field], totalRow)
                  : totalRow[column.field] || ""}
              </td>
            ))}
          </tr>
        </tbody>
      </table>

      {/* Phân trang */}
      <div className="pagination">
        <button
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
          disabled={currentPage === 1}
        >
          Trước
        </button>
        <span>
          Trang {currentPage}/{totalPages}
        </span>
        <button
          onClick={() =>
            setCurrentPage((prev) => Math.min(prev + 1, totalPages))
          }
          disabled={currentPage === totalPages}
        >
          Sau
        </button>
      </div>
    </div>
  );
};

export default Table;
