const ProductList = () => {
  const products = useSelector((state) => state.products);
  const dispatch = useDispatch();
  const [visibleForm, setVisibleForm] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [idSanPham, setIdSanPham] = useState();

  // Tính tổng giá trị của tất cả sản phẩm
  const totalPrice = products.reduce((total, product) => total + product.price, 0);

  const columns = [
    { label: "Tên", field: "name" },
    { label: "Giá", field: "price" },
    {
      label: "Thao tác",
      width: 130,
      render: (val, row) => (
        <>
          <Button
            onClick={() => {
              setVisibleForm(true);
              setIsEdit(true);
              setIdSanPham(row.id);
            }}
            size="small"
            className="primary"
          >
            Chỉnh sửa
          </Button>

          <Tooltip
            content={"Sau khi xóa, dữ liệu sẽ không thể khôi phục lại được"}
            position="left"
          >
            <Button
              onClick={() => dispatch(deleteProduct(row.id))}
              style={{ marginLeft: 8 }}
              size="small"
              className="danger"
            >
              Xóa
            </Button>
          </Tooltip>
        </>
      ),
    },
  ];

  // Dữ liệu hiển thị trong bảng, bao gồm cả hàng "Tổng số"
  const tableData = [
    ...products,
    { id: "total", name: "Tổng số", price: totalPrice, isSummaryRow: true },
  ];

  return (
    <div>
      <Modal onClose={() => setVisibleForm(false)} isOpen={visibleForm}>
        {isEdit ? (
          <EditProductForm
            idSanPham={idSanPham}
            setVisibleForm={setVisibleForm}
          />
        ) : (
          <AddProductForm setVisibleForm={setVisibleForm} />
        )}
      </Modal>
      <h1>Bảng Thông Tin</h1>
      <Tooltip
        content={
          products.length >= 2 ? "Chỉ được phép thêm tối đa 2 mặt hàng" : ""
        }
        position="right"
      >
        <Button
          disabled={products.length >= 2 ? true : false}
          style={{ marginBottom: 8 }}
          size="medium"
          onClick={() => {
            setVisibleForm(true);
            setIsEdit(false);
          }}
        >
          Thêm Hàng Hóa
        </Button>
      </Tooltip>
      <Table columns={columns} data={tableData}></Table>
    </div>
  );
};
